import { ChakraProvider, CSSReset, Box, Button, FormControl, FormLabel, Input, Stack, Heading, Container } from '@chakra-ui/react';
import { useForm } from 'react-hook-form';

function App() {
  const { register, handleSubmit } = useForm();

  const onSubmit = async (data) => {
    try {
      const response = await fetch('http://localhost:3001/api/create-event', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      if (response.ok) {
        console.log('Event created successfully');
      } else {
        console.error('Failed to create event');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <ChakraProvider>
      <CSSReset />
      <Container maxW="container.sm" centerContent>
        <Box p={8} borderWidth={1} borderRadius="lg" boxShadow="lg">
          <Heading as="h2" size="xl" mb={6}>
            EVENT SYNC HUB
          </Heading>
          <form onSubmit={handleSubmit(onSubmit)}>
            <Stack spacing={4}>
              <FormControl>
                <FormLabel>Title</FormLabel>
                <Input {...register('title', { required: true })} />
              </FormControl>
              <FormControl>
                <FormLabel>Date</FormLabel>
                <Input type="date" {...register('date', { required: true })} />
              </FormControl>
              <FormControl>
                <FormLabel>Time</FormLabel>
                <Input type="time" {...register('time', { required: true })} />
              </FormControl>
              <FormControl>
                <FormLabel>Place</FormLabel>
                <Input {...register('place', { required: true })} />
              </FormControl>
              <Button colorScheme="teal" type="submit" mt={4}>
                Create Event
              </Button>
            </Stack>
          </form>
        </Box>
      </Container>
    </ChakraProvider>
  );
}

export default App;
